import React, {useEffect, useRef, useState} from "react";
import "./search.scss";

export default function TrackSearchResults({accessToken,  track, chooseTrack, searchResults, spotifyApi }) {
    const [music, setMusic] = useState()

  function handlePlay() {
    chooseTrack(track);
  }

  useEffect(() => {
    spotifyApi.searchTracks('Love')
    .then(function(data) {
      console.log('Search by "Love"', data.body);
    }, function(err) {
      console.error(err);
    });


  }, [])



  return (

        // {/* {track.artist}
        //     <img src={track.albumUrl} alt="yes" />
        //     {track.title}
        //     <img src={track.bigAlbumImage} alt="yes" /> */}

        <div className="card">
          <div className="cardImage">
            <img src={track.albumUrl} alt="Pic 1" />
          </div>
          <div className="cardContent">
            <h3 onClick={handlePlay}>{track.title}</h3>
            <span>{track.artist}</span>
          </div>
          <span className="playIcon"></span>
        </div>

  );
}
