import React, {useEffect, useRef, UseState} from "react";
import "./search.scss";

export default function TrackSearchResults({ track, chooseTrack }) {
  function handlePlay() {
    chooseTrack(track);
  }

  const [limiter, setLimiter] = useState(0)
  const mainInnerRef = useRef()

  useEffect(() => {
    // function
    const handleWindowResize = () => {
      // calculation
      const calculation = Math.floor(
        mainInnerRef.current.getBoundingClientRect().width / 195
      )

      setLimiter(calculation)
    }

    handleWindowResize()

    // assign event listener
    window.addEventListener('resize', handleWindowResize)

    // remove event listener
    return () => window.removeEventListener('resize', handleWindowResize)
  }, [])

  return (
    <div className="cardsWrap">
      <div className="cardsWrapInner" onClick={handlePlay}>
        {/* {track.artist}
            <img src={track.albumUrl} alt="yes" />
            {track.title}
            <img src={track.bigAlbumImage} alt="yes" /> */}

        <div className="card">
          <div className="cardImage">
            <img src={track.albumUrl} alt="Pic 1" />
          </div>
          <div className="cardContent">
            <h3>{track.title}</h3>
            <span>{track.artist}</span>
          </div>
          <span className="playIcon"></span>
        </div>
      </div>
    </div>
  );
}
