import React, {useState, useEffect} from 'react';
import useAuth from './useAuth'
import SpotifyWebApi from 'spotify-web-api-node'
import TrackSearchResults from './TrackSearchResults';
import Player from './Player';
import Header from './Header';

import './Dashboard.scss'


const spotifyApi = new SpotifyWebApi({
    clientId: '39c957640b2940bc9b1ca9322bf20062'
})


export default function Dashboard({code}) {

    const [search, setSearch] = useState('')
    const [user, setUser] = useState()
    const [searchResults, setSearchResults] = useState([])
    const [playingTrack, setPlayingTrack] = useState()
    const accessToken = useAuth(code)

    function chooseTrack(track) {
        setPlayingTrack(track)
        setSearch('')
    }



    useEffect(() => {
        if (!accessToken) return
        spotifyApi.setAccessToken(accessToken);

        spotifyApi.getMe()
  .then(function(data) {
    console.log('Some information about the authenticated user', data.body);
    setUser(data.body)
  }, function(err) {
    console.log('Something went wrong!', err);
  });

  spotifyApi.getFeaturedPlaylists({ limit : 5, offset: 1, country: 'FR', locale: 'fr_FR', timestamp:'2014-10-23T09:00:00' })
  .then(function(data) {
    console.log(data.body.playlists.items);
  }, function(err) {
    console.log("Something went wrong!", err);
  });


    }, [accessToken])

    useEffect(() => {
        if(!search) return setSearchResults([])
        if(!accessToken) return

        let cancel = false
        spotifyApi.searchTracks(search).then((res) =>{
            if (cancel) return
            setSearchResults(res.body.tracks.items.map(track => {
                const smallestAlbumImage = track.album.images.reduce(
                    (smallest, image) => {
                      if (image.height < smallest.height) return image
                      return smallest
                    },
                    track.album.images[0])
                return {
                    artist: track.artists[0].name,
                    title: track.name,
                    uri: track.uri,
                    albumUrl: smallestAlbumImage.url,
                    bigAlbumImage: track.album.images[0].url
                }
            }))
        })
        return () => cancel = true
    }, [search, accessToken])

    return (
        <div className="dashboard__container">
          <div className="dashboard">
<div>
                <Header search={search} setSearch={setSearch} user={user} />
            </div>




            <div className="cardsWrap">
      <div className="cardsWrapInner" >
    {searchResults.map(track => (
        <TrackSearchResults track={track} user={user} chooseTrack={chooseTrack} key={track.uri} />
    ))}

    {searchResults === 0 && <p>rien</p>}
    </div>
    </div>
          </div>


       <Player  accessToken={accessToken} trackUri={playingTrack?.uri} />
        </div>
    )
}
